import { Location } from '@shared/types/interfaces/location.interface';
import { environment } from '@env';
import { HttpClient } from '@angular/common/http';
import { Component, Input, OnInit } from '@angular/core';
import { Observable, of } from 'rxjs';
import { catchError, map } from 'rxjs/operators';


@Component({
  selector: 'app-map',
  templateUrl: './map.component.html',
  styleUrls: ['./map.component.scss']
})
export class MapComponent implements OnInit {


  @Input() public locations: Location[];
  public apiLoaded: Observable<boolean>;
  public center: google.maps.LatLngLiteral = {lat: 4.6565489, lng: -74.1015251};
  public zoom:number = 14;
  public markerPositions: google.maps.LatLngLiteral[] = [];

  constructor(
    httpClient: HttpClient
    ) {
    this.apiLoaded = httpClient.jsonp(`${environment.urlGoogleMap}${environment.apiKey}`, 'callback')
        .pipe(
          map(() => true),
          catchError(() => of(false))
        );
  }

  ngOnInit(): void {
      this.loadLocations();
  }

  private loadLocations(): void{

    this.markerPositions.push();
  }

}
<div class="map-container">
  <div *ngIf="apiLoaded | async">
    <google-map
    height="25rem"
    width="100%"
    [center]="center"
    [zoom]="zoom">

    </google-map>
  </div>
</div>
